import "./AddUser.css";
import Card from "@material-ui/core/Card";
import { useForm } from "react-hook-form";
import { Container } from "@material-ui/core";

export default function AddUser(){
    const {
        register,
        formState: { errors },
        handleSubmit
      } = useForm();

      async function addUserFun(data){

            let userData = {
              name: data.name,
              avatar: data.avatar,
              createdAt: Date.now()
            };
            await fetch("https://609e2b8c33eed80017957ee5.mockapi.io/users", {
              method: "POST",
              body: JSON.stringify(userData)
            });

      }
      const onSubmit = (data) => {addUserFun(data)}

      return (
          <Card>
        <Container>
          <div className="MyForm">
            <form onSubmit={handleSubmit(onSubmit)}>
              <h1>Create Users</h1>
              <CustomNameInput
                label="Name"
                register={register}
                errors={errors}
              />
              <CustomNameInput
                label="Avatar"
                register={register}
                errors={errors}
              />

              <input type="submit" />
            </form>
          </div>
        </Container>
        </Card>
      );
}
function CustomNameInput({ label, register, errors }) {
    return (
      <>
        <label>{label}</label>
        <input
          placeholder={label}
          {...register(label, {
            required: true,
            minLength: 3
          })}
        />
        {errors[label] && errors[label].type === "required" && (
          <p style={{ color: "red" }}>This input is required</p>
        )}

        {errors[label] && errors[label].type === "minLength" && (
          <p style={{ color: "red" }}>Min Length should be 3</p>
        )}
      </>
    );
  }
