import Card from "@material-ui/core/Card";
import CardHeader from "@material-ui/core/CardHeader";
import Avatar from '@material-ui/core/Avatar';
import { makeStyles } from '@material-ui/core/styles';
import { Container } from "@material-ui/core";


const users=[
    {
     "id": "1",
     "createdAt": "2021-06-09T06:54:25.501Z",
     "name": "name 1",
     "avatar": "https://cdn.fakercloud.com/avatars/scott_riley_128.jpg"
    },
    {
     "id": "2",
     "createdAt": "2021-06-08T12:55:16.298Z",
     "name": "name 2",
     "avatar": "https://cdn.fakercloud.com/avatars/joemdesign_128.jpg"
    },
    {
     "id": "3",
     "createdAt": "2021-06-08T12:11:15.672Z",
     "name": "name 3",
     "avatar": "https://cdn.fakercloud.com/avatars/plbabin_128.jpg"
    },
    {
     "id": "4",
     "createdAt": "2021-06-09T04:16:01.656Z",
     "name": "name 4",
     "avatar": "https://cdn.fakercloud.com/avatars/seyedhossein1_128.jpg"
    }
]

export default function UserList({setCurrentUser}){
    const useStyles = makeStyles((theme) => ({
        large: {
          width: theme.spacing(8),
          height: theme.spacing(8),
        },
      }));
      const classes = useStyles();

    return(
        <>
        {users.map((user)=>{
            return(
                <Container>
                <Card>
                    <CardHeader
                        avatar={
                        <Avatar aria-label="recipe" src={user.avatar} className={classes.large}>
                            R
                        </Avatar>
                        }
                        title={user.name}
                        //  subheader={(new Date(user.createdAt)).toString()}
                        subheader={new Date(user.createdAt).toDateString()}
                    />
                    <button>view profile</button>
                    <button>edit profile</button>
                    <button onClick={()=>{setCurrentUser(user)}}>Set as current User</button>
                </Card>
                <br/>
                </Container>
            )
        })}
        </>
    )
}
